import React, { useState,  useEffect } from 'react'
import {Configuration, OpenAIApi}  from 'openai';


const Search = () => {
const[input, setInput]=useState("");
const[response,setResponse]=useState("")

useEffect(() => {
    // openai.apiKey = "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";
    const configuration = new Configuration({
        apiKey: "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",
      });
      delete configuration.baseOptions.headers['User-Agent'];
      const openai = new OpenAIApi(configuration);
      

      
    const prompt = "What is the weather in London today?";
    openai
      .createCompletion({
        engine: "text-davinci-002",
        prompt: prompt,
      })
      .then(response => {
        console.log(response);
       setResponse(response.choices[0].text);
      });
  }, []);


const handleChange=(e)=>{
   setInput(e.target.value);

}
console.log(input);
  return (
    <input type="text" className="text_input" onChange={handleChange}></input>
  )
}

export default Search
